import 'package:ecommerceapp/common/config.dart';import 'package:ecommerceapp/common/theme/app_css.dart';import 'package:ecommerceapp/extensions/spacing.dart';import 'package:ecommerceapp/extensions/text_style_extensions.dart';import 'package:ecommerceapp/extensions/widget_extension.dart';import 'package:ecommerceapp/widgets/layouts/colorSizeInformationCommon.dart';import 'package:ecommerceapp/widgets/layouts/colorsizeCommonProductOption.dart';import 'package:flutter/material.dart';import 'package:flutter_svg/svg.dart';class ColorSizeCommon extends StatelessWidget {  const ColorSizeCommon({Key? key}) : super(key: key);  @override  Widget build(BuildContext context) {    bool isLiked = false;    return Column(      children: [        ProductOptionsRow(),        const VSpace(Sizes.s35),        Container(          alignment: Alignment.center,          color: Colors.black,          height: 60,          width: double.infinity,          child: Row(            mainAxisAlignment: MainAxisAlignment.center,            children: [              SvgPicture.asset(svgAssets.plus, color: Colors.white),              const HSpace(Sizes.s10),              Text(fonts.addToBasket,                  style: AppCss.tenorSansRegular16.textColor(Colors.white)),              const HSpace(Sizes.s170),              SvgPicture.asset(isLiked == true                      ? svgAssets.whiteHeart                      : svgAssets.heartColor)                  .inkWell(onTap: () {}),            ],          ),        ),        Text(fonts.materials),        Text(fonts.wework),        Text(fonts.care),        Text(fonts.toKeep),        InformationColumn(),      ],    );  }}